    //leetcode submit region begin(Prohibit modification and deletion)
    class Solution {
        public List<Integer> findSubstring(String s, String[] words) {
            int eachLen = words[0].length();
            int wordCnt = words.length;
            int totalLen = eachLen * wordCnt;
            int length = s.length();
            ArrayList<Integer> ans = new ArrayList<>();
//        if (length<totalLen)
//            return ans;
            HashMap<String, Integer> hmap = new HashMap<>();
            for (String word : words) {
                hmap.put(word, hmap.getOrDefault(word, 0)+1);
            }
            for (int i= 0; i < eachLen; i++) {
                int startIdx = i;
                int extra = 0;  // 非word元素的数量
                int diff = hmap.size();  // word元素匹配数
                HashMap<String, Integer> countMap = new HashMap<>();
                int endIdx = startIdx;
                int size = 0;
                while (endIdx+eachLen<=length) {
                    int newEnd = endIdx+eachLen;
                    String substring = s.substring(endIdx, newEnd);
                    countMap.put(substring, countMap.getOrDefault(substring,0)+1);
                    if (hmap.containsKey(substring)) {
                        if (countMap.get(substring).equals(hmap.get(substring))) {
                            diff--;
                        } else if (countMap.get(substring).equals(hmap.get(substring)+1)) {
                            diff++;
                        }
                    }else {
                        extra++;
                    }
                    endIdx = newEnd;
                    size++;
                    if (size>wordCnt) {
                        int newStart = startIdx+eachLen;
                        substring = s.substring(startIdx, newStart);
                        countMap.put(substring, countMap.get(substring)-1);
                        if (hmap.containsKey(substring)) {
                            if (countMap.get(substring).equals(hmap.get(substring))) {
                                diff--;
                            } else if (countMap.get(substring).equals(hmap.get(substring)-1)) {
                                diff++;
                            }
                        }else {
                            extra--;
                        }
                        startIdx = newStart;
                        size--;
                    }
                    if (diff==0 && extra==0) {
                        ans.add(startIdx);
                    }
                }
            }
            return ans;
        }

    }
//leetcode submit region end(Prohibit modification and deletion)

